const chunks = [];
const items = $input.all();

for (const item of items) {
  const data = item.json;
  // Safely extract and combine the transcript text
  const transcriptArray = Array.isArray(data.transcript) ? data.transcript : [];
  const fullText = transcriptArray.map(t => t.text || "").join(" ").trim();

  // Telegram's limit is 4096. We use 4000 to leave room for headers/formatting.
  const chunkSize = 4000;

  for (let i = 0; i < fullText.length; i += chunkSize) {
    chunks.push({
      json: {
        // We add a part number so you can track the sequence in Telegram
        part: (i / chunkSize) + 1,
        total_parts: Math.ceil(fullText.length / chunkSize),
        combinedText: fullText.substring(i, i + chunkSize)
      }
    });
  }
}

return chunks;
